new Vue({
    el: '#admin',
    data: {
        isOpen: true,
        expand: false,
        show: null
    },
    methods: {
        toggleSubMenu: function() {
            if(!this.isOpen) {
                this.isOpen = !this.isOpen;
            }
        },
        toggleMenu: function() {
            this.isOpen = !this.isOpen;
        },
        toggleExpandLess: function() {
            this.expand = !this.expand;
        },
        Meth(e){
            if(!this.show) {
                this.show = e;
            } else {
                this.show = null;
            }
        }
    }
});

$(function(){
	$('#inner-content').slimScroll({
		height: '570px',
        size: '3px',
        alwaysVisible: false
	});
});