new Vue({
    el: '#admin',
    data: {
        isOpen: true,
        expand: false,
        count: 0,
        show: null
    },
    methods: {
        toggleSubMenu: function() {
            if(!this.isOpen) {
                this.isOpen = !this.isOpen;
            }
        },
        toggleMenu: function() {
            this.isOpen = !this.isOpen;
        },
        toggleExpandLess: function() {
            this.expand = !this.expand;
        },
        addToCard(e) {
            const el = document.querySelector('#card'+e);
            //var valu = e.target.dataset.card;
            const {...card} = el.dataset.card.split(',');
            const add = {
                id: card[0],
                article: card[1],
                name: card[2],
                count: card[3],
                price: card[4]
            }
            console.log('to card: ', add);
            //alert('to card: '+s+e);
            //
        },
        Meth(e){
            if(!this.show) {
                this.show = e;
            } else {
                this.show = null;
            }
        }
    }
});

